import React, { useState, useRef, useEffect } from 'react';
import './css/App.css';
import './css/board.css';
import './css/chat.css';
import './css/dropdown.css';
import './css/feature.css';
import './css/header.css';
import './css/hero.css';
import './css/login.css';
import './css/map.css';
import './css/small_translate.css';
import './css/FloatingChatButton.css';
import './css/selfTest.css';
import './css/result.css';
import './css/banner.css';

import Header from './components/Header';
import BannerSlider from './components/BannerSlider';
import Chat from './Chat.js';
import Picture from './Picture.js';
import SelfTest from './components/SelfTest';
import BoardSection from './components/BoardSection';
import ChatModal from './components/ChatModal';
import Footer from './components/Footer';
import AboutSection from './components/AboutSection';
import AuthSection from './components/AuthSection';
import FaqSection from './components/FaqSection';

import { sectionLabels } from './constants/sectionLabels';
import { formInputs } from './constants/formInputs';
import { buttonLabels } from './constants/buttonLabels';
import { formLinks } from './constants/formLinks';

const App = () => {
  const [activeSection, setActiveSection] = useState('about');
  const [selectedBoard, setSelectedBoard] = useState('');
  const [selectedChat, setSelectedChat] = useState(null);
  const [isAdmin] = useState(false);
  const [hoveredMenu, setHoveredMenu] = useState(null);
  const [subMenuVisible, setSubMenuVisible] = useState(null);
  const [visibility, setVisibility] = useState(null);
  const [signupState, setSignupState] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [tab, setTab] = useState('chat');
  const [chatInput, setChatInput] = useState('');
  const [resultText, setResultText] = useState('');
  const [testType, setTestType] = useState('Ïö∞Ïö∏Ï¶ù');
  const [selfAnswers, setSelfAnswers] = useState(Array(20).fill(''));

  const introRef = useRef(null);
  const noticeRef = useRef(null);
  const locationRef = useRef(null);

  const showSection = (section) => {
    setActiveSection(section);
  };

  const handleMouseEnter = (menu) => setHoveredMenu(menu);
  const handleMouseLeaveAll = () => {
    setHoveredMenu(null);
    setSubMenuVisible(null);
  };

  const handleBoardSelect = (boardType) => {
    setSelectedBoard(boardType);
    setActiveSection('board');
  };

  const handleScrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleSelfAnswer = (index, value) => {
    const updatedAnswers = [...selfAnswers];
    updatedAnswers[index] = value;
    setSelfAnswers(updatedAnswers);
  };

  const handleSelfSubmit = (result) => {
    setResultText(result);
  };

  const renderForms = () => {
    if (["login", "id", "password"].includes(activeSection)) {
      return (
        <section className="form-section">
          <h2>{sectionLabels[activeSection]}</h2>
          {formInputs[activeSection].map((input, i) => (
            <input key={i} type={input.type} placeholder={input.placeholder} className="input" />
          ))}
          <button className="button">{buttonLabels[activeSection]}</button>
          {formLinks[activeSection] && (
            <div className="form-links">
              {formLinks[activeSection].map(({ label, id }) => (
                <a key={id} href="#" onClick={() => showSection(id)}>{label}</a>
              ))}
            </div>
          )}
        </section>
      );
    }
    return null;
  };

  return (
    <div>
      <Header
        activeSection={activeSection}
        showSection={showSection}
        hoveredMenu={hoveredMenu}
        handleMouseEnter={handleMouseEnter}
        handleMouseLeaveAll={handleMouseLeaveAll}
        setSubMenuVisible={setSubMenuVisible}
        subMenuVisible={subMenuVisible}
        handleBoardSelect={handleBoardSelect}
        introRef={introRef}
        noticeRef={noticeRef}
        locationRef={locationRef}
      />

      {activeSection === 'faq' && <FaqSection />}

      <div className="floating-sidebar">
        <div className="floating-button1" onClick={() => showSection('faq')}>ÏïàÎÇ¥</div>
        <div className="floating-button1" onClick={() => alert('Ï±óÎ¥á Ìò∏Ï∂ú')}>Î¥á</div>
        <div className="floating-button1" onClick={handleScrollToTop}>‚Üë</div>
      </div>

      {activeSection === 'about' && (
        <>
          <><section ref={introRef} className="section">
            <h2>ÌöåÏÇ¨ ÏÜåÍ∞ú</h2>
            <p>Mind BridgeÎäî Ïù∏Í≥µÏßÄÎä• Í∏∞Î∞ò Ï†ïÏÑú Î∂ÑÏÑù Î∞è ÏÉÅÎã¥ ÏÑúÎπÑÏä§Î•º Ï†úÍ≥µÌï©ÎãàÎã§.</p>
          </section><section ref={noticeRef} className="section">
              <h2>Í≥µÏßÄ ÏÇ¨Ìï≠</h2>
              <p>ÌòÑÏû¨ Ï†ïÍ∏∞ Ï†êÍ≤Ä Ï§ëÏù¥Î©∞, ÏÑúÎπÑÏä§Í∞Ä ÏùºÎ∂Ä Ï†úÌïúÎê† Ïàò ÏûàÏäµÎãàÎã§.</p>
            </section><section ref={locationRef} className="section">
              <h2>ÌöåÏÇ¨ ÏúÑÏπò</h2>
              <div className="map-container">
                <iframe
                  src="https://map.naver.com/p/search/%EC%86%94%EB%8D%B0%EC%8A%A4%ED%81%AC?c=15.00,0,0,0,dh"
                  allowFullScreen
                  className="map-iframe"
                  title="ÌöåÏÇ¨ ÏúÑÏπò" />
                <p className="map-caption">üìç ÏÑúÏö∏ÌäπÎ≥ÑÏãú Ï¢ÖÎ°úÍµ¨ Ï¢ÖÎ°ú12Í∏∏ 15 ÏΩîÏïÑÎπåÎî© 2Ï∏µ, 5Ï∏µ, 8Ï∏µ, 9Ï∏µ, 10Ï∏µ</p>
              </div>
            </section></>
        </>
      )};

      {activeSection === 'self' && (
        <SelfTest
          testType={testType}
          setTestType={setTestType}
          selfAnswers={selfAnswers}
          handleSelfAnswer={handleSelfAnswer}
          handleSelfSubmit={handleSelfSubmit}
          resultText={resultText}
          setSelfAnswers={setSelfAnswers}
        />
      )}

      {activeSection === 'board' && (
        <BoardSection
          selectedBoard={selectedBoard}
          visibility={visibility}
          setVisibility={setVisibility}
        />
      )}

      {activeSection === 'chat' && (
        <section className="chat-section">
          <Chat />
          <Picture />
          <Chat />
        </section>
      )}

      {activeSection === 'email' && (
        <section className="board-section">
          <h2>AI ÏÉÅÎã¥ Í∏∞Î°ù Î©îÏùº Ï†ÑÏÜ°</h2>
        </section>
      )}

      {activeSection === 'signup' && (
        <section className="form-section">
          <h2>{sectionLabels.signup}</h2>
        </section>
      )}

      {activeSection === 'faq' && (
        <section className="form-section">
          <h2>ÏûêÏ£º Î¨ªÎäî ÏßàÎ¨∏</h2>
        </section>
      )}

      {renderForms()}

      <ChatModal
        isOpen={isOpen}
        setIsOpen={setIsOpen}
        tab={tab}
        setTab={setTab}
        selectedChat={selectedChat}
        setSelectedChat={setSelectedChat}
        chatInput={chatInput}
        resultText={resultText}
      />

      <Footer />
    </div>
  );
};

export default App;
