import React from 'react';
import { sectionLabels } from '../constants/sectionLabels';

const Header = ({
  activeSection,
  showSection,
  hoveredMenu,
  handleMouseEnter,
  handleMouseLeaveAll,
  setSubMenuVisible,
  subMenuVisible,
  handleBoardSelect,
  introRef,
  noticeRef,
  locationRef
}) => {
  const scrollToSection = (ref) => {
    if (ref && ref.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header className="header">
      <div id="google_translate_element" className="translate"></div>
      <nav className="nav">
        <div className="nav-left">
          <img
            src="/Î°úÍ≥†2.png"
            alt="Mind Bridge Î°úÍ≥†"
            className="logo"
            onClick={() => showSection('about')}
            style={{ cursor: 'pointer' }}
          />
        </div>
        <div className="nav-center">
          {["about", "services", "board", "self"].map((sec) => (
            <div
              key={sec}
              className="nav-item-wrapper"
              onMouseEnter={() => ["services", "board", "about"].includes(sec) && handleMouseEnter(sec)}
              onMouseLeave={handleMouseLeaveAll}
            >
              <a
                href="#"
                onClick={() => !["services", "board", "about"].includes(sec) && showSection(sec)}
                className={`nav-link ${activeSection === sec && sec !== "about" ? "nav-link-hover" : ""}`}
              >
                {sectionLabels[sec]}
              </a>
              {sec === "about" && hoveredMenu === "about" && (
                <div className="dropdown-wrapper">
                  <div className="dropdown">
                    <div className="dropdown-column">
                      <div className="dropdown-item" onClick={() => scrollToSection(introRef)}>ÌöåÏÇ¨ ÏÜåÍ∞ú</div>
                      <div className="dropdown-item" onClick={() => scrollToSection(noticeRef)}>ÌöåÏÇ¨ Í≥µÏßÄ</div>
                      <div className="dropdown-item" onClick={() => scrollToSection(locationRef)}>ÌöåÏÇ¨ ÏúÑÏπò</div>
                    </div>
                  </div>
                </div>
              )}
              {sec === "services" && hoveredMenu === "services" && (
                <div className="dropdown-wrapper">
                  <div className="dropdown">
                    <div className="dropdown-column">
                      {["ÏÉÅÎã¥", "Í≥†Í∞ù ÏÑúÎπÑÏä§"].map((item, i) => (
                        <div
                          key={i}
                          className={`dropdown-item ${subMenuVisible === item ? "highlight" : ""}`}
                          onMouseEnter={() => setSubMenuVisible(item)}
                        >
                          {item}
                          {subMenuVisible === item && (
                            <div className="dropdown-submenu">
                              {item === "ÏÉÅÎã¥" && (
                                <>
                                  <div className="dropdown-item" onClick={() => showSection("chat")}>AI ÏÉÅÎã¥</div>
                                  <div className="dropdown-item" onClick={() => showSection("email")}>Î©îÏùº</div>
                                </>
                              )}
                              {item === "Í≥†Í∞ù ÏÑúÎπÑÏä§" && (
                                <>
                                  <div className="dropdown-item">ÏÑúÎπÑÏä§ Ï§ÄÎπÑ Ï§ë</div>
                                  <div className="dropdown-item">ÏÑúÎπÑÏä§ Ï§ÄÎπÑ Ï§ë</div>
                                </>
                              )}
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}

              {activeSection === 'about' && (
                <>
                  <><section ref={introRef} className="section">
                    <h2>ÌöåÏÇ¨ ÏÜåÍ∞ú</h2>
                    <p>Mind BridgeÎäî Ïù∏Í≥µÏßÄÎä• Í∏∞Î∞ò Ï†ïÏÑú Î∂ÑÏÑù Î∞è ÏÉÅÎã¥ ÏÑúÎπÑÏä§Î•º Ï†úÍ≥µÌï©ÎãàÎã§.</p>
                  </section><section ref={noticeRef} className="section">
                      <h2>Í≥µÏßÄ ÏÇ¨Ìï≠</h2>
                      <p>ÌòÑÏû¨ Ï†ïÍ∏∞ Ï†êÍ≤Ä Ï§ëÏù¥Î©∞, ÏÑúÎπÑÏä§Í∞Ä ÏùºÎ∂Ä Ï†úÌïúÎê† Ïàò ÏûàÏäµÎãàÎã§.</p>
                    </section><section ref={locationRef} className="section">
                      <h2>ÌöåÏÇ¨ ÏúÑÏπò</h2>
                      <div className="map-container">
                        <iframe
                          src="https://map.naver.com/p/search/%EC%86%94%EB%8D%B0%EC%8A%A4%ED%81%AC?c=15.00,0,0,0,dh"
                          allowFullScreen
                          className="map-iframe"
                          title="ÌöåÏÇ¨ ÏúÑÏπò" />
                        <p className="map-caption">üìç ÏÑúÏö∏ÌäπÎ≥ÑÏãú Ï¢ÖÎ°úÍµ¨ Ï¢ÖÎ°ú12Í∏∏ 15 ÏΩîÏïÑÎπåÎî© 2Ï∏µ, 5Ï∏µ, 8Ï∏µ, 9Ï∏µ, 10Ï∏µ</p>
                      </div>
                    </section></>
                </>
              )};
              {sec === "board" && hoveredMenu === "board" && (
                <div className="dropdown-wrapper">
                  <div className="dropdown">
                    <div className="dropdown-column">
                      <div className="dropdown-item" onClick={() => handleBoardSelect("generalBoard")}>ÏùºÎ∞ò Í≤åÏãúÌåê</div>
                      <div className="dropdown-item" onClick={() => handleBoardSelect("adminBoard")}>Í¥ÄÎ¶¨Ïûê Í≤åÏãúÌåê</div>
                      <div className="dropdown-item" onClick={() => handleBoardSelect("noticeBoard")}>Í≥µÏßÄÏÇ¨Ìï≠</div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
        <div className="nav-right">
          <button onClick={() => showSection("login")} className="auth-button">Î°úÍ∑∏Ïù∏</button>
        </div>
      </nav>
    </header>
  );
};

export default Header;
