export const testQuestions = {
  '우울증': [
    '평소보다 식욕이 없었다', '무슨 일을 해도 기운이 없었다', '사는 게 허무하게 느껴졌다', '자주 울었다',
    '희망을 느끼지 못했다', '평소보다 우울했다', '평소보다 말수가 줄었다', '가족이나 친구에게 짜증을 냈다',
    '밤에 잠을 이루기 어려웠다', '자주 피곤함을 느꼈다', '다른 사람들과 이야기하기 싫었다', '어떤 일에도 집중이 잘 안 되었다'
  ],
  '불안장애': [
    '사소한 일에도 걱정이 많다', '예상치 못한 일에 쉽게 당황한다', '자주 긴장하거나 불안하다',
    '다른 사람 앞에서 말하는 것이 두렵다', '불길한 예감을 자주 느낀다'
  ],
  '스트레스': [
    '평소보다 일이 많고 벅차다', '작은 일에도 예민해진다'
  ]
};

export const getDetailedResult = (testType, score) => {
  const ranges = {
    '우울증': [
      { max: 10, title: '정상', description: '우울 증상이 거의 없습니다.', categories: { '자기관리': ['충분한 수면 유지', '건강한 식사 유지'] } },
      { max: 20, title: '경도 우울', description: '가벼운 우울 증상이 있습니다.', categories: { '도움 제안': ['가벼운 운동 시도', '친구와 대화'] } },
      { max: 36, title: '중증 우울', description: '전문 상담이 필요할 수 있습니다.', categories: { '권장 행동': ['정신과 상담 예약', '심리검사 참여'] } }
    ],
    '불안장애': [
      { max: 5, title: '정상', description: '불안 수준이 낮습니다.', categories: { '스트레스 완화': ['명상', '산책'] } },
      { max: 10, title: '중간', description: '불안감이 존재합니다.', categories: { '권장 사항': ['이완 훈련', '전문가 상담 고려'] } },
      { max: 15, title: '고도 불안', description: '일상 생활에 어려움이 있을 수 있습니다.', categories: { '주의사항': ['약물 치료 검토', '상담 지속'] } }
    ],
    '스트레스': [
      { max: 2, title: '정상', description: '스트레스가 거의 없습니다.', categories: { '유지 전략': ['정기 운동', '적절한 휴식'] } },
      { max: 4, title: '주의', description: '스트레스를 줄일 필요가 있습니다.', categories: { '도움 팁': ['일기 쓰기', '취미 시간 확보'] } },
      { max: 6, title: '위험', description: '즉각적인 스트레스 관리가 필요합니다.', categories: { '필요 조치': ['전문 상담', '업무량 조절'] } }
    ]
  };

  const matched = ranges[testType].find(r => score <= r.max);
  return matched || { title: '알 수 없음', description: '', categories: {} };
};
