const App = () => {
  const [testType, setTestType] = useState('우울증');
  const [selfAnswers, setSelfAnswers] = useState(Array(12).fill(''));
  const [activeSection, setActiveSection] = useState('about');
  const [hoveredMenu, setHoveredMenu] = useState(null);
  const [subMenuVisible, setSubMenuVisible] = useState(null);
  const [selectedChat, setSelectedChat] = useState(null);
  const [chatHistory, setChatHistory] = useState([]);
  const resultText = null;

  const showSection = (section) => {
    setActiveSection(section);
    console.log(`Showing section: ${section}`);
  };

  const scrollToSection = (ref) => {
    if (ref && ref.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleBoardSelect = (boardType) => {
    console.log(`Board selected: ${boardType}`);
  };

  const handleSelfSubmit = () => {
    console.log('제출됨:', selfAnswers);
  };

  const handleRead = () => {
    console.log('텍스트 읽기');
  };

  const handleSendEmail = () => {
    console.log('메일 전송');
  };

  return (
    <div>
      <Header
        activeSection={activeSection}
        setActiveSection={setActiveSection}
        setHoveredMenu={setHoveredMenu}
        hoveredMenu={hoveredMenu}
        setSubMenuVisible={setSubMenuVisible}
        subMenuVisible={subMenuVisible}
        showSection={showSection}
        scrollToSection={scrollToSection}
        handleBoardSelect={handleBoardSelect}
      />

      <BannerSlider />

      <SelfTest
        testType={testType}
        setTestType={setTestType}
        selfAnswers={selfAnswers}
        setSelfAnswers={setSelfAnswers}
        handleSelfSubmit={handleSelfSubmit}
        resultText={resultText}
      />

      <ChatModal />
      <EmailSection
        chatHistory={chatHistory}
        selectedChat={selectedChat}
        setSelectedChat={setSelectedChat}
        handleRead={handleRead}
        handleSendEmail={handleSendEmail}
      />
      <BoardSection />
      <FloatingSidebar />
      <Footer />
    </div>
  );
};
